import React,{useState} from "react";
import './styles.css'

const TodoItem = (props) => {
  const [todos, setTodos] = useState(props.todos)
  const handleDelete = (i) =>{
      setTodos( todos.filter((item,i)=>{
        return item.id !== i
      }))
  }
  return (
    <li
      style={{ width: "40%", margin: "0px auto", padding: "10px" }}
      className="border d-flex justify-content-between align-items-center mb-4"
    >
      <div>
  <h6>{props.index+1}_ {props.title}</h6>
        <span style={{ color: "gray" }}>{props.detail}</span>
      </div>

      <button type="button" className="close" onClick={()=>handleDelete(props.index)}>
        <span>&times;</span>
      </button>
    </li>
  );
};

export default TodoItem;
