import React from 'react';
import TodoItem from './TodoItem'

const TodoList = (props) => {
    const del = (title) =>{
        props.del(title)
    }
    return (
        <ul className="list-group" style={{width: "40%", margin: "0px auto",listStyle:'none'}}>
            {
                props.todos.map((item,i)=>{
                    if(item.todoTitle===''&& item.todoDetail===''){
                        return <li style={{padding: "10px",backgroundColor:'rgb(52, 58, 64)',color:'white' }}>Input Fields Should Not Be Empty</li>
                    }else{
                        return <TodoItem key={i} index={i} title={item.todoTitle} detail={item.todoDetail} del={del}/> 
                    }

                })
            }
        </ul>
    );
}

export default TodoList;
