import logo from './logo.svg';
import Header from './components/Header'
import Footer from './components/Footer'
import Form from './components/Form'
import TodoList from './components/TodoList'
import {useState,useEffect} from 'react'
import './App.css';

const getItemsFromLS = () =>{
  const todos =  localStorage.getItem('todos')

  if(todos){
    return JSON.parse(localStorage.getItem('todos'))
  }else{
    return null
  }

}


function App() {
  const [todos, setTodos] = useState(getItemsFromLS())
  const [mode, setMode] = useState('light')

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const submitTodo = (todo) =>{

    setTodos([...todos,todo])
  }

  const del = (title) =>{

    setTodos(todos.filter((item)=>{
      return item.todoTitle !== title
    }))
  }

  const handleToggle = () =>{

    // setMode(((curr)=>curr==='light'?'dark':'light'))
    document.body.style.backgroundColor='red'
  }

  return <div>
            <Header mode={mode} toggle={handleToggle}/>
            <Form addTodo={submitTodo}/>
            <TodoList todos={todos} del={del}/>
            <Footer/>
          </div>
}

export default App;
